CREATE TABLE "public"."leave" ("id" serial NOT NULL, "user_id" uuid NOT NULL, "start_date" timestamptz NOT NULL, "end_date" timestamptz NOT NULL, "type_id" integer NOT NULL, "reason" text NOT NULL, "status" text NOT NULL DEFAULT 'Pending', PRIMARY KEY ("id") , FOREIGN KEY ("user_id") REFERENCES "public"."user"("id") ON UPDATE restrict ON DELETE restrict, FOREIGN KEY ("type_id") REFERENCES "public"."leave_type"("id") ON UPDATE restrict ON DELETE restrict);
